import Configuration from '../models/Configuration';

export default {
    addConfiguration: async (req, res, next) => {
        try {

            const { aboutInfo } = req.body;
            const newConfiguration = new Configuration({
                aboutInfo
            })

            const newConfigurationSaved = await newConfiguration.save();
            res.status(200).json(newConfigurationSaved);

        } catch (error) {
            res.status(500).json({
                message: 'Ocurri un error'
            });
            next();
        }
    },
    listConfiguration: async (req, res, next) => {
        try {
            const settings = await Configuration.find();
            res.status(200).json(settings);
            
        } catch (error) {
            res.status(500).json({
                message: 'Ocurri un error'
            });
            next();
        }
    }
}

