import mongoose, { Schema } from 'mongoose';

const ConfigurationSchema = new Schema({
    logoURL: {
        public_id: {
            type: String
        },
        imageURL: {
            type: String
        }
    },
    socialMedia: {
        facebook: {
            type: String
        },
        instagram: {
            type: String
        },
        twitter: {
            type: String
        },
        google: {
            type: String
        },
        youtube: {
            type: String
        }
    },
    aboutInfo: {
        type: String
    },
    contactInfo: {
        type: String
    },
    companyName: {
        type: String,
        maxlength: 64
    }
},
    {
        timestamps: true,
        collection: 'Configuration'
    });

const Configuration = mongoose.model('Configuration', ConfigurationSchema)

export default Configuration;

